/* ************************************************************************** */
/*                                                                            */
/*                                                        :::      ::::::::   */
/*   Main.js                                            :+:      :+:    :+:   */
/*                                                    +:+ +:+         +:+     */
/*   By: macbook <macbook@student.42.fr>            +#+  +:+       +#+        */
/*                                                +#+#+#+#+#+   +#+           */
/*   Created: 2023/03/10 22:36:44 by macbook           #+#    #+#             */
/*   Updated: 2023/03/12 20:32:56 by macbook          ###   ########.fr       */
/*                                                                            */
/* ************************************************************************** */

import * as React from "react";
import Task from "./Task.js";
import AddIcon from "@mui/icons-material/Add";
import { createClient } from "@supabase/supabase-js";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myTask: [],
      counter: 0,
    };
    this.addNewTask = this.addNewTask.bind(this);
  }
  handleKeyPress = (event) => {
    if (event.key === "Enter") {
      this.addNewTask();
    }
  };
  async intiData() {

  }
  async addNewTask() {
    let taskValue;
    this.state.counter++;
    this.intiData();
    if (this.state.counter < 5)
      return ;
    console.log("in : ", this.state.counter);
    taskValue = document.getElementById("input-task").value;
    if (taskValue === "") {
      taskValue = "New Task..";
    }
    console.log(this.state.counter);
    const myTask = [
      ...this.state.myTask,
      <Task
        key={this.state.myTask.length}
        data={taskValue}
        id={this.state.myTask.length}
      />,
    ];
    this.setState({
      myTask: myTask,
    });
    document.getElementById("input-task").value = "";
    document.getElementById("tasks-container").classList.add("bg-white", "border", "border-gray-300");
  }

  checkCounter() {
    console.log('checkCounter', this.state.counter);
    if (this.state.counter === 3) {
      this.addNewTask();
    }
  }
  render() {
    this.state.counter++;
    if (this.state.counter === 2) {
      this.addNewTask();
    }
    const { myTask } = this.state;
    return (
      <div className="w-full flex justify-center py-4">
        <div className="w-[90%] tablet:w-[60%] desktop:w-[40%]">
          <div className="flex justify-center w-full">
            <div className="flex justify-center items-center w-full border border-gray-300 bg-white rounded-lg">
              <input
                onKeyPress={ this.handleKeyPress }
                id="input-task"
                placeholder="Add new task"
                className="w-full rounded-lg p-4 focus-within:outline-none rounded-r-none bg-white"
              ></input>
              <div className="hover:fill-gray-500 cursor-pointer transit h-full flex items-center p-4 rounded-lg rounded-l-none">
                <AddIcon onClick={ this.addNewTask } className="" />
              </div>
            </div>
          </div>
          <div onLoad={ this.checkCounter() } id="tasks-container" className="my-4 rounded-lg  p-4 py-8">
            {myTask && myTask}
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
