/* ************************************************************************** */
/*                                                                            */
/*                                                        :::      ::::::::   */
/*   Main.js                                            :+:      :+:    :+:   */
/*                                                    +:+ +:+         +:+     */
/*   By: macbook <macbook@student.42.fr>            +#+  +:+       +#+        */
/*                                                +#+#+#+#+#+   +#+           */
/*   Created: 2023/03/10 22:36:44 by macbook           #+#    #+#             */
/*   Updated: 2023/03/11 08:30:43 by macbook          ###   ########.fr       */
/*                                                                            */
/* ************************************************************************** */

import * as React from "react";
import Task from "./Task";
import AddIcon from "@mui/icons-material/Add";

class Main extends React.Component {
    constructor(props) {
        super(props);        
        this.state = {
          myTask: null
        };
        this.addNewTask = this.addNewTask.bind(this);
    }
  addNewTask(params) {
    let taskValue = document.getElementById("input-task").value;
    const myTask = <Task data={ taskValue }/>
    this.setState({
        myTask: myTask
    });
    this.setState({
        myTask: null
    });

  }
  render() {
    const { myTask } = this.state
    return (
      <div className="w-full flex justify-center py-4">
        <div className="w-4/5">
          <div className="flex justify-center w-full">
            <div className="flex justify-center items-center w-full shadow">
              <input
                id="input-task"
                placeholder="Add new task"
                className="w-full rounded p-4 focus-within:outline-none rounded-r-none"
              ></input>
              <div className="cursor-pointer bg-white h-full flex items-center p-4 rounded rounded-l-none">
                <AddIcon
                  onClick={this.addNewTask}
                  className="hover:scale-110"
                />
              </div>
            </div>
          </div>
          <div className="my-8 p-4 bg-white rounded shadow">
            { myTask && myTask }
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
