/* ************************************************************************** */
/*                                                                            */
/*                                                        :::      ::::::::   */
/*   Main.js                                            :+:      :+:    :+:   */
/*                                                    +:+ +:+         +:+     */
/*   By: macbook <macbook@student.42.fr>            +#+  +:+       +#+        */
/*                                                +#+#+#+#+#+   +#+           */
/*   Created: 2023/03/10 22:36:44 by macbook           #+#    #+#             */
/*   Updated: 2023/03/12 03:14:33 by macbook          ###   ########.fr       */
/*                                                                            */
/* ************************************************************************** */

import * as React from "react";
import Task from "./Task.js";
import AddIcon from "@mui/icons-material/Add";
import { createClient } from '@supabase/supabase-js'



class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myTask: [],
    };
    this.addNewTask = this.addNewTask.bind(this);
  }

  handleKeyPress = (event) => {
    if (event.key === "Enter") {
        this.addNewTask();
    }
  };
  addNewTask(task_data) {
    let taskValue = document.getElementById("input-task").value;;
    if(task_data != null)
      taskValue = task_data
    if (taskValue === "")
      return;
    const myTask = [
      ...this.state.myTask,
      <Task key={this.state.myTask.length} data={taskValue} id={ this.state.myTask.length } />,
    ];
    this.setState({
      myTask: myTask,
    });
    document.getElementById("input-task").value = "";
    document.getElementById('tasks-container').classList.add('bg-white', 'border', 'border-gray-300');
  }
  render() {
    const { myTask } = this.state;
    return (
      <div className="w-full flex justify-center py-4">
        <div className="w-[90%] tablet:w-[60%] desktop:w-[40%]">
          <div className="flex justify-center w-full">
            <div className="flex justify-center items-center w-full border border-gray-300 bg-white rounded-lg">
              <input
                onKeyPress={this.handleKeyPress}
                id="input-task"
                placeholder="Add new task"
                className="w-full rounded-lg p-4 focus-within:outline-none rounded-r-none bg-white"
              ></input>
              <div className="hover:fill-gray-500 cursor-pointer transit h-full flex items-center p-4 rounded-lg rounded-l-none">
                <AddIcon
                  onClick={this.addNewTask}
                  className=""
                />
              </div>
            </div>
          </div>
          <div id="tasks-container" className="my-4  rounded-lg  p-4 py-8">
            {myTask && myTask}
          </div>
        </div>
      </div>
    );
  }
}

const supabaseUrl = "https://irxramvfitikwpoziyku.supabase.co"
const supabaseAnonKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImlyeHJhbXZmaXRpa3dwb3ppeWt1Iiwicm9sZSI6ImFub24iLCJpYXQiOjE2Nzg1NjE5MDksImV4cCI6MTk5NDEzNzkwOX0.kbh9nYZGLslw57uVGJfnDHNZYznJUdWWz-vsUGsvFAQ"
const supabase = createClient(supabaseUrl, supabaseAnonKey)
async function main() {
  let { data: Tasks, error } = await supabase.from('Tasks').select('*');
  if(!(error))
  {
    Tasks.forEach(element => {
      let myMain = new Main;
      myMain.addNewTask(element.text);
      console.log(element.text);
    });
  }
}
main();

export default Main;
