/* ************************************************************************** */
/*                                                                            */
/*                                                        :::      ::::::::   */
/*   Main.js                                            :+:      :+:    :+:   */
/*                                                    +:+ +:+         +:+     */
/*   By: macbook <macbook@student.42.fr>            +#+  +:+       +#+        */
/*                                                +#+#+#+#+#+   +#+           */
/*   Created: 2023/03/10 22:36:44 by macbook           #+#    #+#             */
/*   Updated: 2023/03/11 13:45:13 by macbook          ###   ########.fr       */
/*                                                                            */
/* ************************************************************************** */

import * as React from "react";
import { ReactDOM } from "react";
import Task from "./Task";
import AddIcon from "@mui/icons-material/Add";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myTask: [],
    };
    this.addNewTask = this.addNewTask.bind(this);
  }

  handleKeyPress = (event) => {
    if (event.key === "Enter") {
        this.addNewTask();
    }
  };
  addNewTask() {
    let taskValue = document.getElementById("input-task").value;
    document.getElementById('tasks-container').classList.add('bg-white', 'shadow');
    const myTask = [
      ...this.state.myTask,
      <Task key={this.state.myTask.length} data={taskValue} id={ this.state.myTask.length } />,
    ];
    this.setState({
      myTask: myTask,
    });
    document.getElementById("input-task").value = "";
  }
  render() {
    const { myTask } = this.state;
    return (
      <div className="w-full flex justify-center py-4">
        <div className="w-4/5">
          <div className="flex justify-center w-full">
            <div className="flex justify-center items-center w-full shadow rounded-lg">
              <input
                onKeyPress={this.handleKeyPress}
                id="input-task"
                placeholder="Add new task"
                className="w-full rounded-lg p-4 focus-within:outline-none rounded-r-none"
              ></input>
              <div className="cursor-pointer transit bg-white h-full flex items-center p-4 rounded-lg rounded-l-none">
                <AddIcon
                  onClick={this.addNewTask}
                  className="hover:scale-110"
                />
              </div>
            </div>
          </div>
          <div id="tasks-container" className="my-8 rounded-lg">
            {myTask && myTask}
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
