/* ************************************************************************** */
/*                                                                            */
/*                                                        :::      ::::::::   */
/*   Task.js                                            :+:      :+:    :+:   */
/*                                                    +:+ +:+         +:+     */
/*   By: macbook <macbook@student.42.fr>            +#+  +:+       +#+        */
/*                                                +#+#+#+#+#+   +#+           */
/*   Created: 2023/03/11 07:00:20 by macbook           #+#    #+#             */
/*   Updated: 2023/03/11 19:15:06 by macbook          ###   ########.fr       */
/*                                                                            */
/* ************************************************************************** */

import * as React from "react";
import LongMenu from './LongMenu';
import Checkbox from '@mui/material/Checkbox';

export default function Task(props) {
  function checkBoxFuntion(){
    let checkId = document.getElementById("checkbox-id-" + props.id);
    let taskText = document.getElementsByClassName("taskText")[checkId.value];
    taskText.classList.toggle('line-through');
    taskText.classList.toggle('text-gray-500');
  }
  setTimeout(() => {
    document.getElementById("task-id-" + props.id).classList.remove('opacity-0');
  }, 50);
  return (
    <div id={ "task-id-" + props.id } className="transit opacity-0 tasks-class p-1 border-b flex items-center justify-between mb-1 ">
      <div className="flex items-center overflow-scroll">
        <div>
          <Checkbox color="default" className="opacity-40" value={ props.id } id={ "checkbox-id-" + props.id } onChange={checkBoxFuntion} />
        </div>
        <div className="taskText overflow-scroll">
            { props.data }
        </div>
      </div>
      <div>
        <LongMenu />
      </div>
    </div>
  );
}
